package librasigner_test

import (
	"testing"

	"github.com/libra/libra-client-sdk-go/librakeys"
	"github.com/libra/libra-client-sdk-go/librasigner"
	"github.com/libra/libra-client-sdk-go/libratypes"
	"github.com/libra/libra-client-sdk-go/stdlib"
	"github.com/libra/libra-client-sdk-go/testnet"
	"github.com/stretchr/testify/assert"
)

func TestSign(t *testing.T) {
	var maxGasAmount uint64 = 1000000
	var gasUnitPrice uint64 = 0
	var seq uint64 = 42
	var expiration uint64 = 1593189628
	var amount uint64 = 100
	var currencyCode = "LBR"

	sender := newKeysFromHexKeys("fc4ea02dc1e42b332ac221d716ece959d5b1fc86c156fa4a5d8b77b3886c3c63", "b38318e91089220c144854881c48b88975c25d6395ac3aeeb21a287bcfa1ebe9fc4ea02dc1e42b332ac221d716ece959d5b1fc86c156fa4a5d8b77b3886c3c63")
	receiver := newKeysFromHexKeys(
		"a761194c93feb3983e6fffb0af9ccc02bc91fe21e1a9c38b24e03dabc40105ed",
		"6762610fdb4bc8acee054bf11870277c63386d64a22ae67a90936e74cb6c4ccba761194c93feb3983e6fffb0af9ccc02bc91fe21e1a9c38b24e03dabc40105ed",
	)

	script := stdlib.EncodePeerToPeerWithMetadataScript(
		libratypes.Currency(currencyCode),
		receiver.AccountAddress(),
		amount, []byte{}, []byte{})

	txn := librasigner.Sign(
		sender,
		sender.AccountAddress(),
		seq,
		script,
		maxGasAmount, gasUnitPrice, currencyCode,
		expiration,
		testnet.ChainID,
	)
	expected := ""
	assert.Equal(t, expected, libratypes.ToHex(txn))
}

func newKeysFromHexKeys(publicKeyHex string, privateKeyHex string) *librakeys.Keys {
	publicKey, err := librakeys.NewEd25519PublicKeyFromString(publicKeyHex)
	if err != nil {
		panic(err)
	}
	privateKey, err := librakeys.NewEd25519PrivateKeyFromString(privateKeyHex)
	if err != nil {
		panic(err)
	}
	return librakeys.NewKeysFromPublicAndPrivateKeys(publicKey, privateKey)
}
